ll f[62][2][3];

vector<int> calc(ll n, ll r, ll x) {
  vector<int> cur(3);
  if(r < 0) {
    return cur;
  }
  memset(f, 0, sizeof f);
  f[60][1][0] = 1;
  for(int i = 59; ~i; i--) {
    for(int j = 0; j < 3; j++) {
      if(f[i + 1][0][j]) {
	f[i][0][(j << 1) % 3] ^= 1;
	if(n >> i & 1) {
	  f[i][0][((j << 1) | 1) % 3] ^= 1;
	}
      }
      if(f[i + 1][1][j]) {
	if(r >> i & 1) {
	  f[i][0][(j << 1) % 3] ^= 1;
	  if(n >> i & 1) {
	    f[i][1][(j << 1 | 1) % 3] ^= 1;
	  }
	} else {
	  f[i][1][(j << 1) % 3] ^= 1;
	}
      }
    }
  }
  x = (x % 3 + 3) % 3;
  for(int i = 0; i < 3; i++) {
    cur[(x + i) % 3] = f[0][0][i] ^ f[0][1][i];
  }
  return cur;
}

