  }
  return ans[pos];
}

bool lightnow(ll x, ll y) {
  if(y < 0 || y > x) {
    return 0;
  }
  return (x & y) == y;
}

ll limit(ll cur, ll op) {
  for(int i = 60; ~i; i--) {
    bool light = false;
    ll tmp = cur + (op << i);
    for(int j = 1; j <= n; j++) {
      light ^= lightnow(y[j] + inf, tmp - x[j]);
    }
    if(light) {
      cur = tmp;
    }
  }
  return cur;
}

