    // Replacement for cin.
    // The >> operator is implemented optimizing it for integer types and strings.
    class FastInput {
      public:
        // Various specializations of >>.
        FastInput& operator >> (char& c) {
            c = NextChar();
            return *this;
        }

        FastInput& operator >> (char* str) {
            if (SkipBlanks()) {
                if (PeekChar()) {
                    *(str++) = PeekChar();
                    while (!IsBlank(NextChar())) *(str++) = PeekChar();
                }
                *(str++) = 0;
            }
            return *this;
        }

        FastInput& operator >> (string& str) {
            if (SkipBlanks()) {
                str.clear();
                str += PeekChar();
                while (!IsBlank(NextChar())) str += PeekChar();
            }
            return *this;
        }

        FastInput& operator >> (double& d) {
            static char buffer[1<<10];
            if ((*this) >> buffer) sscanf(buffer, "%lf", &d);
            return *this;
        }

        template <typename intType>
        FastInput& operator >> (intType& n) {
            if (SkipBlanks()) {
                int sign = +1;
                if (PeekChar() == '-') {
                    sign = -1;
                    n = NextChar() - '0';
                } else
                    n = PeekChar() - '0';
                while (!IsBlank(NextChar())) {
                    n += n + (n << 3) + PeekChar() - 48;
                }
                n *= sign;
            }
            return *this;
        }

        // The empty method tie is useful to ignore calls to cin.tie(0).
        void tie(void*) {}

      private:
        // Low level methods.
        char cur;
        char NextChar() {
            return cur = getc_unlocked(stdin);
            // return cur = _getchar_nolock(); // Supported by Windows.
        }
        char PeekChar() { return cur; }

        // Input routines.
        operator bool() {
            return PeekChar();
        }

        static bool IsBlank(char c) {
            // 10 9 32 are ascii numbers for \n, \t, space.
            return (c <= 32 && c);
        }

        bool SkipBlanks() {
            while (IsBlank(NextChar()));
            return PeekChar() != 0;
        }
    } __fast_input__;

    // Replacement for cout.
    // The << operator is implemented optimizing it for integer types and strings.
    class FastOutput {
      public:
        // Various specializations of <<.
        FastOutput& operator << (char c) {
            PutChar(c);
            return *this;
        }

        FastOutput& operator << (const char * s) {
            while (*s) PutChar(*s++);
            return *this;
        }

        FastOutput& operator << (const string & s) {
            for (char c : s) PutChar(c);
            return *this;
        }

        FastOutput& operator << (double d) {
            return (*this) << ToString(d);
        }

        template <typename intType>
        FastOutput& operator << (intType n) {
            return (*this) << ToString(n);
        }
      private:
        // Low level methods.
        void PutChar(char c) {
            putc_unlocked(c, stdout);
            // _putchar_nolock(c); // Supported by Windows.
        }

        // ToString routines.
        const char* ToString(double d) {
            static char buffer[1<<6];
            sprintf(buffer, "%lf%c", d, '\0');
            return buffer;
        }

        template <typename intType>
        const char * ToString(intType n) {
            static char buffer[1<<6];
            char* p = (buffer + 30);
            if (n) {
                bool is_neg = 0;
                if (n < 0) {
                    is_neg = 1;
                    n = -n;
                }
                while (n) {
                    *--p = (n % 10) + '0';
                    n /= 10;
                }
                if (is_neg) *--p = '-';
            } else *--p = '0';
            return p;
        }
    } __fast_output__;

    // Defines replacing cin/cout with FastInput/FastOutput.
    #undef cin  // To ignore reading from input.txt on local machine?
