bool count(ll y, ll m){
	if(y==0) return 1;
	else if(y>0){
		bool ans=0;
		for(int i=58; i>=0; i--){
			if(m&(1ll<<i)){
				if(!(y&((1ll<<i)-1))){
					ans^=1;
				}
				if(!(y&(1ll<<i))) break;
			}
		}
		if((m|y)==y) ans^=1;
		return ans;
	}else{
		y=-y;
		y--;
		if(y==0){
			return (m+1)&1ll;
		}
		m+=y;
		bool myon=0, ans=0;
		for(int i=58; i>=0; i--){
			if(m&(1ll<<i)) myon=1;
			if(y&(1ll<<i) && !myon){
				return 0;
			}
			if(m&(1ll<<i)){
				if(y&(1ll<<i)) continue;
				if((y&((1ll<<i)-1))==((1ll<<i)-1)) ans^=1;
			}else{
				if(y&(1ll<<i)) break;
			}
		}
		if((m|y)==m) ans^=1;
		return ans;
	}
}
