unsigned long long msb(unsigned long long x) {
	x |= x >> 1;
	x |= x >> 2;
	x |= x >> 4;
	x |= x >> 8;
	x |= x >> 16;
	x |= x >> 32;
	return x & ~(x >> 1);
}

signed main() {
	INIT;

	VAR(int, n);

	std::map<int, std::set<int>> map;

	REP(i, n) {
		VAR(int, x, y);
		map[x].insert(y);
	}

	std::priority_queue<int, std::vector<int>, std::greater<int>> pq;
	for (auto& p : map) pq.push(p.first);

	int preX = -INFLL;
	while (!pq.empty()) {
		int x = pq.top(); pq.pop();
		if (preX == x) continue;
		preX = x;
		auto it = map.find(x);
		if (it == map.end()) continue;
		auto& set = it->second;

		if (set.size() % 2 == 1) {
			OUT(x)BR;
			return 0;
		}

		std::priority_queue<int, std::vector<int>, std::greater<int>> pq2;
		for (auto& x : set) pq2.push(x);

		while (!pq2.empty()) {
			int y = pq2.top(); pq2.pop();
			if (!set.count(y)) continue;
			set.erase(y);
			int d = pq2.top() - y;
			int M = msb(d);
			if (d == M) {
				pq2.pop();
				set.erase(y + M);
			}
			else {
				set.insert(y + M);
				pq2.push(y + M);
			}
			if (!map.count(x + M)) pq.push(x + M);
			auto& set2 = map[x + M];
			if (set2.count(y)) set2.erase(y);
			else set2.insert(y);
		}
	}

	return 0;
}

