ll binary_search(function<bool(ll)> func, ll start, ll end) {
    /*      func:int ->bool
            returns smallest int x where func(x) evaluates to true, searches in [start,end), it is assumed the values are false, .. , false, true ...
             */
    if (end <= start) {   return end;  }   // has to be here, otherwise func(end-1) in next line could be a problem
    if (!func(end-1)) {  return end;  }
    while (end-start>1) {
        ll mid = (start+end)/2;
        if (func(mid)) {  end = mid;  } else { start = mid;   }
    }
    if (func(start)) {  return start;  } else {  return end;   }
};

template<class T>
ll ternary_search(function<T(ll)> func, ll left, ll right) {
    // Find maximum of function func within [left, right]

    while (true) {
        // Left and right are the current bounds; the maximum is between them
//        cout << right << " " << left << endl;
        if (right - left <= 2) {  break; }

        ll left_third = left + (right - left) / 3;
        ll right_third = right - (right - left) / 3;

        if (func(left_third) < func(right_third)) {
            left = left_third;
        } else {
            right = right_third;
        }
    }
    if (right-left == 0) {  return right;  }
    if (right-left == 1) {  if (func(left) >= func(right)) {  return left;  } else {  return right;  }   }
    if ((func(left) >= func(left+1)) && (func(left) >= func(left+2))) {  return left;  }
    if ((func(left+1) >= func(left)) && (func(left+1) >= func(left+2))) {  return (left+1);  }
    return (left+2);
};


int binomialmod2(ll a, ll b) {
    if ((b<0) || (b>a)) {  return 0;  }
    int ret = 1;
    while (b>0) {
        int digb = b%2, diga = a%2;
        a/=2; b/=2;
        if (digb>diga) { ret = 0; break; }
    }
    return ret;
};

int no_of_pts(vector<pair<ll,ll>>& pts, ll y_min, ll x) {  // return no of pts less or equal than x (mod 2)
  int ret = 0;
  for(auto p : pts) {
      ll x_ = p.first, y_ = p.second;
      if ((x >= x_) && (x <= x_+y_-y_min)) {
          ll a = y_-y_min, b = x-x_;
          ret += binomialmod2(a-1,b);
          ret = ret % 2;
      }
  }
  return ret;
};

