int binomMod2(long n, long k) {
  return ((n & k) == k) ? 1 : 0;
}

// XOR{ x + k | C(y, k) is odd }
long calcBrute(long x, long y) {
  const lim = cast(size_t)(y + 1);
  auto bn = new int[][lim];
  foreach (i; 0 .. lim) {
    bn[i] = new int[i + 1];
    bn[i][0] = bn[i][i] = 1;
    foreach (j; 1 .. i) {
      bn[i][j] = bn[i - 1][j - 1] ^ bn[i - 1][j];
    }
  }
  long ret;
  foreach (k; 0 .. y + 1) {
    if (bn[cast(size_t)(y)][cast(size_t)(k)]) {
      ret ^= (x + k);
    }
  }
  return ret;
}
long calc(long x, long y) {
  long ret;
  for (long l = 1; l - x <= y; l <<= 1) {
    if (y <= l && binomMod2(x, l - y)) {
      ret ^= l;
    }
  }
  return ret;
}


