#!/usr/bin/env python
# -*- coding: utf-8 -*-
import itertools
import copy


def is_pair(a, b):
  d = abs(a - b)
  x = 1

  while x <= d:
    if x == d:
      return True

    x *= 2

  return False


def search(coods):
  while len(coods) > 1:
    pairs = set()
    targets = {c: 0 for c in coods}

    for (x1, y1), (x2, y2) in itertools.combinations(coods, 2):
      x3, y3 = None, None

      if is_pair(x1, x2) and y1 == y2:
        x3 = min(x1, x2)
        y3 = y1 + abs(x1 - x2)
      elif x1 == x2 and is_pair(y1, y2):
        x3 = x1 + abs(y1 - y2)
        y3 = min(y1, y2)
      elif is_pair(x1, x2) and abs(x1 - x2) == abs(y1 - y2):
        x3 = min(x1, x2)
        y3 = min(y1, y2)

      if x3 is not None:
        pairs.add(((x1, y1), (x2, y2), (x3, y3), max(abs(x1 - x2), abs(y1 - y2))))
        targets[(x1, y1)] += 1
        targets[(x2, y2)] += 1

    if len(pairs) == 0:
      return None

    pairs = [(p1, p2, p3, d, min(targets[p1], targets[p2])) for p1, p2, p3, d in pairs]
    pairs.sort(key=lambda x: (x[4], x[3]))

    if pairs[0][4] == 1:
      p1, p2, p3, _, _ = pairs[0]
      coods.remove(p1)
      coods.remove(p2)
      coods.add(p3)
      continue

    for p1, p2, p3, _, _ in pairs:
      clone = copy.copy(coods)
      clone.remove(p1)
      clone.remove(p2)
      clone.add(p3)

      start = search(clone)

      if start is not None:
        return start

  return list(coods)[0]


def main():
  n = int(input())
  coods = set([tuple(map(int, input().split())) for _ in range(n)])
  start = search(coods)

  print(start[0], flush=True)


if __name__ == '__main__':
  main()

