      elif x1 == x2 and is_pair(y1, y2):
        x3 = x1 + abs(y1 - y2)
        y3 = min(y1, y2)
      elif is_pair(x1, x2) and abs(x1 - x2) == abs(y1 - y2):
        x3 = min(x1, x2)
