    int n,m;
    cin >> n >> m;
    bool a[n][m];
    int r = 0;
    for (int i = 0;i < n;++i) for (int j = 0;j < m;++j) cin >> a[i][j];
    for (int i = 0;i < n;++i) {
        int p = i,q = i;
        for (int j = i;j < n;++j) {
            if (a[p][q]) break;
            for (int k = i;k < m;++k) if (a[j][k]) {
                p = j;q = k;
                break;
            }
