    for (int i = 0; i < n; ++i) {
        res *= 2;
        res %= mod;
    }
    return res;
}

bool operator < (const bitset<max_n> &a, const bitset<max_n> &b) {
    for (int i = 0; i < m; ++i) {
        if (a[i] != b[i]) {
            return a[i] < b[i];
        }
    }
    return false;
