void add(bitset<max_n> b) {
    for (int i = 0; i < v.size(); ++i) {
        if ((b ^ v[i]) < b) {
            b ^= v[i];
        }
    }
    if (b == 0) {
        return;
    }
    for (int i = 0; i < v.size(); ++i) {
        if ((b ^ v[i]) < v[i]) {
            v[i] ^= b;
        }
    }
    v.push_back(b);
}

