template <typename T>
std::size_t Rank(Matrix<T> mat)
{
    std::size_t pos = 0;
    for (std::size_t i = 0; i < mat.C; i++) {
        if (pos == mat.R) { return pos; }
        std::size_t piv = pos;
        for (std::size_t k = pos + 1; k < mat.R; k++) {
            if (mat[k][i] != 0) { piv = k; }
        }
        if (mat[piv][i] == 0) { continue; }
        std::swap(mat[pos], mat[piv]);
        for (std::size_t j = i + 1; j < mat.C; j++) { mat[pos][j] /= mat[pos][i]; }
        for (std::size_t j = pos + 1; j < mat.R; j++) {
            for (std::size_t k = i + 1; k < mat.C; k++) { mat[j][k] -= mat[pos][k] * mat[j][i]; }
        }
        pos++;
    }
    return pos;
}
constexpr ll MOD = 998244353;
int main()
{
    std::size_t N, M;
    std::cin >> N >> M;
    Matrix<int> a(N, M);
    for (std::size_t i = 0; i < N; i++) {
        for (std::size_t j = 0; j < M; j++) { std::cin >> a[i][j]; }
    }
    const std::size_t R = Rank(a);
    std::vector<ll> bp(600, 1LL);
    for (std::size_t i = 1; i < 600; i++) { bp[i] = bp[i - 1] * 2 % MOD; }
    std::cout << (bp[N + M - 1] - bp[N + M - R - 1] + MOD) % MOD << std::endl;
    return 0;
}

