using MMK = IntMod<998244353>;

int N, M;
int A[300][300];
MMK Naive() {
	MMK ret = 0;
	REP(s, 0, 1 << N) {
		REP(t, 0, 1 << M) {
			int cnt = 0;
			REP(i, 0, N) {
				REP(j, 0, M) {
					if (!((1 << i) & s)) continue;
					if (!((1 << j) & t)) continue;
					cnt += A[i][j];
				}
			}
			if (cnt % 2 == 1) {
				++ret;
			}
		}
	}
	return ret;
}

int Rank() {
	int r = 0;
	REP(i, 0, N) {
		int k = r;
		while (k < N && A[k][i] == 0) {
			++k;
		}
		if (k == N) continue;
		if (k != i) {
			REP(j, 0, N) {
				swap(A[i][j], A[k][j]);
			}
		}

		REP(k, i + 1, N) {
			if (A[k][i] == 1) {
				REP(j, i, N) {
					A[k][j] ^= A[i][j];
				}
			}
		}
		++r;
	}
	return r;
}

