const double __gauss_template_EPS = 1e-8;

int Gauss(vector<vector<int>> &mat) {
    // return rank
    int rank = 0;
    int n = (int)mat.size();

    for(int i = 0; i < n; i++) {
        bool ok = false;
        for(int j = rank; j < n; j++) {
            if(mat[j][i]) {
                swap(mat[j], mat[rank]);
                ok = true;
                break;
            }
        }

        if(!ok) continue;

        int fs = mat[rank][i];

        for(int j = rank+1; j < n; j++) {
            int r = mat[j][i] / fs;

            for(int k = i; k < n; k++) {

                mat[j][k] ^= mat[rank][k] * r;
            }
        }

        rank++;
    }

    return rank;
}


