template <class T>
int gaussian_elimination(matrix<T>& a, bool is_extended = false) {
    int m = a.size(), n = a[0].size();
    int rank = 0;
    T det = 1;
    for (int col = 0; col < n; ++col) {
        if (is_extended && col == n - 1) break;
        int pivot = -1;
        T mx = 1e-5;
        for (int row = rank; row < m; ++row) {
            if (abs(a[row][col]) > mx) {
                mx = abs(a[row][col]);
                pivot = row;
            }
        }
        if (pivot == -1) continue;
        swap(a[pivot], a[rank]);
        auto fac = a[rank][col];
        det *= fac;
        for (int col2 = 0; col2 < n; col2++) a[rank][col2] /= fac;
        for (int row = 0; row < m; row++) {
            if (row != rank && abs(a[row][col]) > 1e-9) {
                auto fac = a[row][col];
                for (int col2 = 0; col2 < n; col2++) {
                    a[row][col2] -= a[rank][col2] * fac;
                    a[row][col2] %= 2;
                    if (a[row][col2] < 0) a[row][col2] += 2;
                }
            }
        }
        rank++;
    }
    return rank;
    // return (m == rank ? det : 0);
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    int n, m;
    cin >> n >> m;
    matrix<int> a(n, m);
    rep(i, 0, n) rep(j, 0, m) cin >> a[i][j];
    int r = gaussian_elimination(a);
    debug(r);
    mint ans = (mint)2 ^ (mint)(n + m - 1);
    ans -= (mint)2 ^ (mint)(n + m - r - 1);
    cout << ans << endl;
    return 0;
}

