template <typename T>
std::size_t Rank(Matrix<T> mat)
{
    std::size_t r = 0;
    for (std::size_t c = 0; c < mat.C; c++) {
        if (r == mat.R) { break; }
        std::size_t piv = r;
        for (; piv < mat.R and mat[piv][c] == 0; piv++) {}
        if (piv == mat.R) { continue; }
        std::swap(mat[r], mat[piv]);
        const T p = mat[r][c];
        for (std::size_t i = r; i < mat.R; i++) { mat[i][c] /= p; }
        for (std::size_t j = 0; j < mat.C; j++) {
            if (j == c) { continue; }
            const T p = mat[r][j];
            for (std::size_t i = 0; i < mat.R; i++) { mat[i][j] -= mat[i][c] * p; }
        }
        r++;
    }
    return r;
}
constexpr int MOD = 998244353;
int main()
{
    std::size_t N, M;
    std::cin >> N >> M;
    Matrix<int> a(N, M);
    for (std::size_t i = 0; i < N; i++) {
        for (std::size_t j = 0; j < M; j++) { std::cin >> a[i][j]; }
    }
    const std::size_t R = Rank(a);
    auto pow = [](auto&& self, const long long p, const std::size_t n) -> long long { return n == 0 ? 1LL : n % 2 == 1 ? self(self, p, n - 1) * p % MOD : self(self, p * p % MOD, n / 2); };
    std::cout << (pow(pow, 2, N + M - 1) - pow(pow, 2, N + M - R - 1) + MOD) % MOD << std::endl;
    return 0;
}

