#include <bits/stdc++.h>
using namespace std;
using Int = long long;
const Int MOD = 998244353;
const Int MAX = 1000;
Int fac[MAX], finv[MAX], inv[MAX];
void COMinit() {
    fac[0] = fac[1] = 1;
    finv[0] = finv[1] = 1;
    inv[1] = 1;
    for (int i = 2; i < MAX; i++) {
        fac[i] = fac[i - 1] * i % MOD;
        inv[i] = MOD - inv[MOD % i] * (MOD / i) % MOD;
        finv[i] = finv[i - 1] * inv[i] % MOD;
    }
}
Int COM(Int n, Int k) {
    if (n < k || n < 0 || k < 0) return 0;
    return fac[n] * (finv[k] * finv[n - k] % MOD) % MOD;
}
Int POW(Int n, Int m) {
    if (m == 0) return 1;
    if (m % 2 == 0) return POW(n * n % MOD, m / 2);
    return n * POW(n, m - 1) % MOD;
}
int gaussian_elimination(vector<vector<int>> &A) {
    int N = A.size(), M = A[0].size();
    int t = 0;
    for (int i = 0; i < N; i++) {
        for (; t < M; t++) {
            for (int j = i; j < N; j++) {
                if (A[j][t]) {
                    swap(A[i], A[j]);
                    break;
                }
            }
            if (A[i][t]) break;
        }
        if (t >= M) return i;
        for (int j = i + 1; j < N; j++) {
            if (j != i && A[j][t]) {
                for (int k = 0; k < M; k++) A[j][k] ^= A[i][k];
            }
        }
        t++;
    }
    return N;
}
int main() {
    cin.tie(nullptr); ios::sync_with_stdio(false);
    COMinit();
    int N, M; cin >> N >> M;
    vector<vector<int>> A(N, vector<int>(M));
    for (auto &row : A) for (auto &a : row) cin >> a;
    int rank = gaussian_elimination(A);
    Int ans = 0;
    for (int i = 1; i <= rank; i++) {
        int t = (i + 1) / 2;
        ans = ans + COM(rank, i) * t % MOD * POW(2, M - t) % MOD;
    }
    ans %= MOD;
    ans = ans * POW(2, N - rank) % MOD;
    cout << ans << endl;
    return 0;
}

