template <typename T> int rank_matrix(vector<vector<T>> mat){

  int n = mat.size();
  int m = mat[0].size();
  int p = 0;


  REP(i,n){
    if(p>=m) break;    

    bool b = true;
    FOR(j,i,n) if(mat[j][p] != 0) b = false;

    if(b){
      ++p;
      continue;
    }

    if(mat[i][p] == 0){
      FOR(j,i+1,n){
	if(mat[j][p] != 0){
	  swap(mat[i], mat[j]);
	  break;
	}
      }
    }

    FOR(j,i+1,n){
      T t = mat[j][p] / mat[i][p];
      REP(k,m) mat[j][k] -= mat[i][k] * t;
    }
  }

  int r = 0;
  REP(i,n){
    REP(j,m){
      if(mat[i][j] != 0){
	++r;
	break;
      }
    }
  }

  return r;
}


