MATRIX<T> operator+(const MATRIX<T> &A, const MATRIX<T> &B) {
	MATRIX<T> C{A};
	C+=B;
	return C;
}
template<class T>
MATRIX<T> operator-(const MATRIX<T> &A, const MATRIX<T> &B) {
	MATRIX<T> C{A};
	C-=B;
	return C;
}
// O( n^2 )
template<class T>
VECTOR<T> operator*(const MATRIX<T> &A, const VECTOR<T> &x) {
	VECTOR<T> y(A.n);
	for (int i = 0; i < A.n; ++i)
		for (int j = 0; j < A.m; ++j)
			y[i] += T(A[i][j])*x[j];
	return y;
}
// O( n^3 )
template<class T>
MATRIX<T> operator*(const MATRIX<T> &A, const MATRIX<T> &B) {
	MATRIX<T> C({A.n,B.m});
	for (int i = 0; i < C.n; ++i)
		for (int j = 0; j < C.m; ++j)
			for (int k = 0; k < A.m; ++k)
				C[i][j] += T(A[i][k])*B[k][j];
	return C;
}
// O( n^3 )
template<class T>
void operator*=(MATRIX<T> &A, const MATRIX<T> &B){
	A = A*B;
}

// O( n^3 log e )
template<class T>
MATRIX<T> pow(const MATRIX<T> &A, long long e) {
	return e == 0 ? identity<T>(A.size())  :
	e % 2 == 0 ? pow(A*A, e/2) : A*pow(A, e-1);
}

