matrix identity1(int n,int m) {
	matrix A(n, array1(m));
	return A;
}
int rank1(matrix A) {
	const int n = A.size(), m = A[0].size();
	int r = 0;
	for (int i = 0; r < n && i < m; ++i) {
		int pivot = r;
		for (int j = r + 1; j < n; ++j)
			if (abs(A[j][i]) > abs(A[pivot][i])) pivot = j;
		swap(A[pivot], A[r]);
		if (abs(A[r][i]) < eps) continue;
		for (int k = m - 1; k >= i; --k)
			A[r][k] /= A[r][i];
		for (int j = r + 1; j < n; ++j)
			for (int k = i; k < m; ++k)
				A[j][k] -= A[r][k] * A[j][i];
		++r;
	}
	return r;
}

ll mpow(int n, int k, int p) {
	if (k == 0)return 1;
	if (k % 2)return 1LL * n * mpow(n, k - 1, p) % p;
	else {
		ll sub = mpow(n, k / 2, p);
		return 1LL * sub * sub% p;
	}
}

int main() {
	int n, m; cin >> n >> m;
	vector<array1> a=identity1(n,m);
	rep(i, 0, n) {
		rep(j, 0, m) cin >> a[i][j];
	}
	ll r = rank1(a);
	ll ans = (mpow(2, n + m - 1, MOD) - mpow(2, n + m - r - 1, MOD) + MOD) % MOD;
	cout << ans << endl;
	return 0;
}

