#include <bits/stdc++.h>

#define MOD 998244353

using namespace std;

int pow(int x, int pow) {
	int res = 1, t = x;
	while (pow) {
		if (pow & 1) {
			res = 1LL * res * t % MOD;
		}
		t = 1LL * t * t % MOD;
		pow >>= 1;
	}
	return res;
}

int main() {
	int n, m;
	cin >> n >> m;
	int** mat = new int*[n];
	for (int i = 0; i < n; i++) {
		mat[i] = new int[m];
		for (int j = 0; j < m; j++) {
			cin >> mat[i][j];
		}
	}
	int cur = 0;
	for (int c = 0; cur < n && c < m; c++) {
		for (int r = cur; r < n; r++) {
			if (mat[r][c]) {
				swap(mat[cur], mat[r]);
				break;
			}
		}
		if (!mat[cur][c]) {
			continue;
		}
		for (int r = 0; r < n; r++) {
			if (r != cur && mat[r][c]) {
				for (int k = c; k < m; k++) {
					mat[r][k] ^= mat[cur][k];
				}
			}
		}
		cur++;
	}

	int tot = pow(2, n + m - 1);
	tot -= pow(2, n + m - cur - 1);
	if (tot < 0) {
		tot += MOD;
	}
	cout << tot << endl;

	for (int i = 0; i < n; i++) {
		delete mat[i];
	}
	delete mat;
	return 0;
}

