int gauss_jordan(vector<bitset<300>> &a){
    int n = a.size(), m = 300;
    int cnt = 0;
    for (int i = 0; i < m && cnt != n; ++i) {
        int p = cnt;
        for (int j = cnt; j < n; ++j) {
            if(a[j][i]){
                p = j;
                break;
            }
        }
        if((a[p][i]) == 0) continue;
        swap(a[cnt], a[p]);
        for (int j = 0; j < n; ++j) {
            if(cnt == j) continue;
            if(a[j][i]) a[j] ^= a[cnt];
        }
        cnt++;
    }
    a.erase(a.begin()+cnt, a.begin()+n);
    return cnt;
}

