int64_t pw(int n, int k) {
	if (k==0)
		return 1;

	int64_t tmp = pw(n, k/2);

	if (k%2)
		return tmp * tmp % MOD * n % MOD;
	return tmp * tmp % MOD;
}

void solve() {
	for (int i=0; i<m; ++i) {
		for (int j=i; j<n; ++j)
			if (e[j].coeff[i])
				swap(e[i], e[j]);

		for (int j=i+1; j<n; ++j) {
			if (e[j].coeff[i]==0)
				continue;
			for (int k=0; k<m; ++k)
				e[i].coeff[k] ^= e[i].coeff[k];
			e[j].val ^= e[i].val;
		}
	}
