	int64_t tmp = 1;

	for (int i=0; i<m; ++i) {
		if (i>=n || e[i].coeff[i]==0)
			tmp = tmp * 2 % MOD;
	}

	cout << pw(2, n-1) * (pw(2, m) - tmp + MOD) % MOD;
}

