void RowReduction(vvi& a)
{
	int m=a.size(),n=a[0].size();
	for(int i=0,j=0;i<m&&j<n;){
		int p=-1;
		repi(k,i,m) if(a[k][j]){
			p=k;
			break;
		}
		if(p==-1){
			j++;
			continue;
		}
		swap(a[i],a[p]);
		rep(k,m) if(k!=i&&a[k][j])
			repi(l,j,n)
				a[k][l]^=a[i][l];
		i++,j++;
	}
}

