void swapRows(int mat[305][305], int i, int j, int w)
{
	for(int k = 0; k < w; k++) swap(mat[i][k], mat[j][k]);
}

void GaussianElimination(int mat[305][305], int w, int h)
{
	int r = 0;
	for(int i = 0; i < w && r < h; i++){
		if(mat[i][r] == 0){
			int max_val = 0, max_j;
			for(int j = r+1; j < h; j++){
				if(mat[j][i] > max_val){
					max_val = mat[j][i];
					max_j = j;
				}
			}
			if(max_val == 0) goto end;
			swapRows(mat, r, max_j, w);
		}

		for(int j = 0; j < h; j++){
			if(j == r) continue;
			if(mat[j][i] == 0) continue;
			for(int k = 0; k <= w; k++){
				mat[j][k] ^= mat[r][k];
			}
		}
		r++;
		end:;
	}
}

