	for(int i = 0; i < h-rank; i++) nsize *= 2, nsize %= mod;
	for(int i = 0; i < h; i++) dsize *= 2, dsize %= mod;
	llint ans = (dsize - nsize + mod) % mod;
	for(int i = 0; i < w-1; i++) ans *= 2, ans %= mod;

	cout << ans << endl;
	return 0;
}

