const int MAXN = 307;
const int MOD  = 998244353;
inline int add(int x, int y) {
	return (x + y >= MOD ? x + y - MOD : x + y);
}
inline int sub(int x, int y) {
	return (x - y < 0 ? x - y + MOD : x - y);
}
inline int mult(int x, int y) {
	ll r = x * 1LL * y;
	if(r >= MOD) r %= MOD;
	return (int)r;
}
int pw2[MAXN];
bitset<MAXN> G[MAXN], basis[MAXN];
int n, m;
inline int GaussAdd(bitset<MAXN> v) {
	for(int i = 0; i < MAXN; ++i) if(v[i]) {
		if(basis[i].count()) {
			v ^= basis[i];
		} else {
			basis[i] = v;
			return 1;
		}
	}
	return 0;
}
void solve() {
	pw2[0] = 1;
	for(int i = 1; i < MAXN; ++i) pw2[i] = add(pw2[i - 1], pw2[i - 1]);
	cin >> n >> m;
	for(int i = 0; i < n; ++i) for(int j = 0; j < m; ++j) G[i][j] = fetch<int>();
	int szBasis = 0;
	for(int i = 0; i < n; ++i) szBasis += GaussAdd(G[i]);
	int countZero = pw2[n - szBasis];
	int countNonZero = sub(pw2[n], countZero);
	int answ = mult(countNonZero, pw2[m - 1]);
	cout << answ << '\n';
}

int main() {
	fast_io();
	solve();

	return 0;
}

