template<typename T>
T binpow(T n, T s)
{
    if (s <= 0)
        return 1LL;
    if (s % 2 == 0){
        T b = binpow(n, s / 2);
        return ( 1LL * b * b ) % base2;
    } else {
        return (1LL* binpow(n, s - 1) * n) % base2;
    }
}

const int N = 305;

int gauss (vector < bitset<N> > a, int n, int m) {    
    vector<int> where (m, -1);

    int rank = max(n, m);
    for (int col=0, row=0; col<m && row<n; ++col) {
        for (int i=row; i<n; ++i)
            if (a[i][col]) {
                swap (a[i], a[row]);
                break;
            }
        if (!a[row][col]) {
            rank--;
            continue;
        }
        where[col] = row;

        for (int i=0; i<n; ++i)
            if (i != row && a[i][col])
                a[i] ^= a[row];
        ++row;
    }

    return rank;
}

