using mat = vector<bitset<305>>;
mat gauss_jordan(mat a) {
    const int h = a.size(), w = a[0].size();
    int rank = 0;
    REP(i, min(w, h)) {
        int pivot = -1;
        FOR(j, rank, h) if(a[j][i]) {pivot = j; break;}
        if(pivot == -1) continue;
        swap(a[rank], a[pivot]);
        REP(j, h) if(j != rank && a[j][i]) a[j] ^= a[rank];
        rank++;
    }
    return a;
}

