        if(b) a[i].set(j);
    }

    auto ret = gauss_jordan(a);
    ll rank = h;
    REP(i, h) {
        bool flag = false;
        REP(j, w) if(ret[i][j]) flag = true;
        if(!flag) {
            rank = i;
