        }
    }

    // (2^h - 2^(h-rank)) * 2^(w-1)
    ll ans = ((binpow(2, h) - binpow(2, h-rank)) % MOD + MOD) % MOD;
    (ans *= binpow(2, w-1)) %= MOD;
    cout << ans << endl;

    return 0;
}

