vector< bitset<N> > basis;

void updBasis(bitset<N> val) {
    for (int i = 0; i < (int)basis.size(); ++i) {
        if (!val.count() ) return ;
        auto _ = basis[i];
        int f_ = -1;
        for (int j = 0; j < N; ++j) if (_[j]) f_ = j;
        int fVal = -1;
        for (int j = 0; j < N; ++j) if (val[j]) fVal = j;
        if (f_ == fVal) val ^= _;
        else {
            if (f_ < fVal) basis.insert(basis.begin() + i, val);
            else basis.insert(basis.begin() + i + 1, val);
            return ;
        }
    }
    if (val.count() ) basis.push_back(val);
}

int add(int _a, int _b) {
    _a += _b;
    if (_a >= mod) _a -= mod;
    return _a;
}
int sub(int _a, int _b) { return add(_a, mod - _b); }
int mul(int _a, int _b) { return (int)( (ll)_a * _b % mod); }

