    pow2[0] = 1; for (int i = 1; i < (N << 1); ++i) pow2[i] = (int)(2 * pow2[i - 1] % mod);

    cout << sub(pow2[n + m - 1], pow2[n + m - 1 - (int)basis.size()]);

