template <typename T>
std::size_t Rank(Matrix<T> mat)
{
    std::size_t pos = 0;
    for (std::size_t i = 0; i < mat.C; i++) {
        if (pos == mat.R) { break; }
        std::size_t piv = pos;
        for (std::size_t k = pos + 1; k < mat.R; k++) {
            if (mat[k][i] != 0) { piv = k; }
        }
        if (mat[piv][i] == 0) { continue; }
        std::swap(mat[pos], mat[piv]);
        for (std::size_t j = i + 1; j < mat.C; j++) { mat[pos][j] /= mat[pos][i]; }
        for (std::size_t j = pos + 1; j < mat.R; j++) {
            for (std::size_t k = i + 1; k < mat.C; k++) { mat[j][k] -= mat[pos][k] * mat[j][i]; }
        }
        pos++;
    }
    return pos;
}
