            r[i] += a[i][j], c[j] += a[i][j];
        }
    }
    for (int i = 1; i <= n; i++)
    {
        dpr[i][0][0] = dpc[i][0][0] = 1;
        for (int j = 1; j <= m; j++)
        {
            dpr[i][j][0] = dpr[i][j - 1][0];
            dpr[i][j][1] = dpr[i][j - 1][1];
            if (a[i][j] & 1)
            {
                dpr[i][j][0] = (dpr[i][j][0] + dpr[i][j-1][1]) % MOD;
                dpr[i][j][1] = (dpr[i][j][1] + dpr[i][j-1][0]) % MOD;
            }
            else
            {
                dpr[i][j][0] = dpr[i][j][0] * 2 % MOD;
