                dpc[j][i][1] = (dpc[j][i][1] + dpc[j][i-1][0]) % MOD;
            }
            else
            {
                dpc[j][i][0] = dpc[j][i][0] * 2 % MOD;
                dpc[j][i][1] = dpc[j][i][1] * 2 % MOD;
            }
        }
    }
