const int INF = 1 << 29;
const int MOD = 998244353;

int64 power_mod(int64 a, int n) {
  int64 ret = 1;
  for (; n; n >>= 1) {
    if (n & 1) ret = ret * a % MOD;
    a = a * a % MOD;
  }
  return ret;
}

const int N = 300 + 10;
int a[N][N];
int gauss(int a[N][N], int n, int m) {
  int i;
  for (i = 0; i < n && i < m; ++i) {
    int p, q;
    for (p = i; p < n; ++p) {
      for (q = i; q < m; ++q) {
        if (a[p][q]) break;
      }
      if (q < m) break;
    }
    if (p == n) break;
    if (p != i) {
      for (int j = i; j < m; ++j) {
        swap(a[i][j], a[p][j]);
      }
    }
    if (q != i) {
      for (int j = i; j < n; ++j) {
        swap(a[j][i], a[j][q]);
      }
    }
    for (int j = i + 1; j < n; ++j) {
      if (a[j][i]) {
        for (int k = i; k < m; ++k) {
          a[j][k] ^= a[i][k];
        }
      }
    }
  }
  return i;
}

int main() {
  int n, m;
  scanf("%d%d", &n, &m);
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < m; ++j) {
      scanf("%d", &a[i][j]);
    }
  }
  int rk = gauss(a, n, m);
  int ret = (power_mod(2, n) + MOD - power_mod(2, n - rk)) *
    power_mod(2, m - 1) % MOD;
  printf("%d\n", ret);
  return 0;
}

