}

BinaryMatrixBoolReference& BinaryMatrixBoolReference::operator=(bool value) {
	if (value) {
		var_m |= mask_m;
	} else {
		var_m &= ~mask_m;
	}
	return *this;
}

BinaryMatrixBoolReference& BinaryMatrixBoolReference::operator|=(bool value) {
	if (value) {
		var_m |= mask_m;
	}
	return *this;
}

BinaryMatrixBoolReference& BinaryMatrixBoolReference::operator&=(bool value) {
	if (!value) {
		var_m &= ~mask_m;
	}
	return *this;
}

