	return var_m & mask_m;
}

void BinaryMatrixBoolReference::Flip() {
	var_m ^= mask_m;
}

void BinaryMatrixBoolReference::Swap(BinaryMatrixBoolReference& other) {
	if (*this == other) return;
	Flip();
	other.Flip();
}

