	: size_m(size), arr_m((size + BITS - 1) / BITS) {
}

BinaryMatrixRow& BinaryMatrixRow::operator+=(const BinaryMatrixRow& right) {
	assert(size_m == right.size_m);

	for (int i = 0; i < arr_m.size(); ++i) {
		arr_m[i] ^= right.arr_m[i];
	}
	return *this;
}

BinaryMatrixRow::Reference BinaryMatrixRow::operator[](int index) {
	int pos = index / BITS;
	int bit = index % BITS;
	return Reference(arr_m[pos], bit);
