	std::fill(arr_m.begin(), arr_m.end(), 0);
}

void BinaryMatrixRow::Flip() {
	for (uint64_t& value : arr_m) {
		value = ~value;
	}

	//  0 
	int rest_bit = size_m % BITS;
	if (rest_bit == 0) return;

	uint64_t mask = ((uint64_t)1 << rest_bit) - 1;
	arr_m.back() &= mask;
}

BinaryMatrixRow::Iterator BinaryMatrixRow::begin() {
	return Iterator(arr_m.data(), 0);
