	}
	return *this;
}

BinaryMatrix& BinaryMatrix::operator-=(const BinaryMatrix& right) {
	*this += right;
	return *this;
}

BinaryMatrix& BinaryMatrix::operator*=(const BinaryMatrix& right) {
