BinaryMatrix BinaryMatrix::Trans() const {
	BinaryMatrix ret(width_m, height_m);

	for (int i = 0; i < height_m; ++i) {
		for (int j = 0; j < width_m; ++j) {
			ret.mat_m[j][i] = mat_m[i][j];
		}
	}

