/*  */
BinaryMatrix BinaryMatrix::Inverse() const {
	assert(Is_square());

	const int size = height_m;
	BinaryMatrix extended = Join_horizontally(*this, BinaryMatrix::Identity(size));
	const auto& res = Reduce_by_gauss(extended, size);

	if (res.rank < size) throw std::runtime_error("matrix not invertible");
	return res.matrix.Block(0, size, size, 2 * size);
}

bool BinaryMatrix::Determinant() const {
	assert(Is_square());

	const auto& res = Reduce_by_gauss(*this);
	return res.determinant;
}

int BinaryMatrix::Rank() const {
	const auto& res = Reduce_by_gauss(*this);
	return res.rank;
}

/*  */
BinaryMatrix BinaryMatrix::Block(int top, int bottom, int left, int right) const {
	BinaryMatrix ret(bottom - top, right - left);

	for (int i = top; i < bottom; ++i) {
		for (int j = left; j < right; ++j) {
			ret.mat_m[i - top][j - left] = mat_m[i][j];
		}
	}
	return ret;
}

bool BinaryMatrix::Is_addable(const BinaryMatrix& left, const BinaryMatrix& right) {
	return left.height_m == right.height_m && left.width_m == right.width_m;
}

bool BinaryMatrix::Is_multipliable(const BinaryMatrix& left, const BinaryMatrix& right) {
	return left.width_m == right.height_m;
}

/* : (bit ) */
int BinaryMatrix::Compare(const BinaryMatrix& left, const BinaryMatrix& right) {
	if (left.height_m < right.height_m) return -1;
	if (left.height_m > right.height_m) return 1;
	if (left.width_m < right.width_m) return -1;
	if (left.width_m > right.width_m) return 1;

	const int M = left.height_m;
	for (int i = 0; i < M; ++i) {
		int res_comp = BinaryMatrixRow::Compare(left.mat_m[i], right.mat_m[i]);
		if (res_comp != 0) return res_comp;
	}
	return 0;
}

BinaryMatrix BinaryMatrix::Multiply(const BinaryMatrix& left, const BinaryMatrix& right) {
	assert(Is_multipliable(left, right));

	const int L = left.height_m;
	const int M = left.width_m;
	const int N = right.width_m;

	BinaryMatrix ret(L, N);

	const BinaryMatrix tr = right.Trans();
	for (int i = 0; i < L; ++i) {
		for (int j = 0; j < N; ++j) {
			ret.mat_m[i][j] = (left.mat_m[i] + right.mat_m[j]).Count() % 2;
		}
	}

	return ret;
}

BinaryMatrix BinaryMatrix::Identity(int size) {
	BinaryMatrix ret(size, size);

	for (int i = 0; i < size; ++i) {
		ret.mat_m[i][i] = 1;
	}
	return ret;
}

/*  */
BinaryMatrix BinaryMatrix::Join_horizontally(const BinaryMatrix& left, const BinaryMatrix& right) {
	assert(left.height_m == right.height_m);

	BinaryMatrix ret(left.height_m, left.width_m + right.width_m);

	for (int i = 0; i < left.height_m; ++i) {
		for (int j = 0; j < left.width_m; ++j) {
			ret.mat_m[i][j] = left.mat_m[i][j];
		}
	}

	for (int i = 0; i < right.height_m; ++i) {
		for (int j = 0; j < right.width_m; ++j) {
			ret.mat_m[i][j + left.width_m] = right.mat_m[i][j];
		}
	}

	return ret;
}

BinaryMatrix BinaryMatrix::Join_vertically(const BinaryMatrix& top, const BinaryMatrix& bottom) {
	assert(top.width_m == bottom.width_m);

	BinaryMatrix ret(top.height_m + bottom.height_m, top.width_m);

	for (int i = 0; i < top.height_m; ++i) {
		ret.mat_m[i] = top.mat_m[i];
	}

	for (int i = 0; i < bottom.height_m; ++i) {
		ret.mat_m[i + top.height_m] = bottom.mat_m[i];
	}

	return ret;
}

