BinaryMatrixReductionInfo Reduce_by_gauss(const BinaryMatrix& mat_arg, int row_max) {
	BinaryMatrix mat = mat_arg;
	if (row_max == -1) {
		row_max = mat.Width();
	}

	const int M = mat.Height();
	const int N = mat.Width();

	int rank = 0;
	for (int row = 0; row < row_max; ++row) {
		/*  */
		int pivot_i = -1;
		for (int i = rank; i < M; ++i) {
			if (mat[i][row]) {
				pivot_i = i;
				break;
			}
		}

		/*  curr_j  0  */
		if (pivot_i == -1) continue;

		/*  */
		if (pivot_i != rank) {
			using std::swap;
			swap(mat[pivot_i], mat[rank]);		// 
		}

		/*  */
		for (int i = 0; i < M; ++i) {
			if (i == rank) continue;
			if (!mat[i][row]) continue;

			mat[i] += mat[rank];
		}
		++rank;
	}

	BinaryMatrixReductionInfo ret;
	ret.matrix = std::move(mat);
	ret.rank = rank;
	ret.determinant = (rank == M);
	return ret;
}
