void setkj(int n) {
	kj[0] = 1;
	rep(i, n)kj[i + 1] = kj[i] * (i + 1) % mod;
	rep(i, n + 1)kji[i] = modpow(kj[i], mod - 2);
}
int comb(int r, int c) {
	if (c<0 || r<c)return 0;
	return kj[r] * kji[c] % mod*kji[r - c] % mod;
}
int GJ(mat &A) {
	int N = A.size(), M = A[0].size();
	int rnk = 0;
	rep(i, M) {
		int pivot = -1;
		for (int j = rnk; j < N; j++) {
			if (A[j][i]) {
				pivot = j; break;
			}
		}
		if (pivot == -1)continue;
		swap(A[pivot], A[rnk]);

		rep(j, N) {
			if (j != rnk&&A[j][i]) {
				rep(k, M) {
					A[j][k] ^= A[rnk][k];
				}
			}
		}
		rnk++;
	}

	int res = 0;
	rep(i, N) {
		rep(j, M) {
			if (A[i][j]) {
				res++; break;
			}
		}
	}
	return res;
}

