const Number eps = 0;

int mat_rank(Matrix A) {
    const int n = A.size(), m = A[0].size();
    int r = 0;
    for (int i = 0; r < n && i < m; ++i) {
        int pivot = r;
        for (int j = r + 1; j < n; ++j) {
            if (A[j][i]) {
                pivot = j;
                break;
            }
        }
        swap(A[pivot], A[r]);
        if (A[r][i] <= eps) {
            continue;
        }
        for (int j = r + 1; j < n; ++j) {
            if (A[j][i]) {
                for (int k = i; k < m; ++k) {
                    A[j][k] ^= A[r][k];
                }
            }
        }
        ++r;
    }
    return r;
}

