class Solver2 {
 public:
  int n, m;
  vector<vector<int>> a;
  Solver2(LL n, LL m) : n(n), m(m), a(n, vector<int>(m)){};

  int sweep(vector<vector<int>> X) {
    int rank = 0;
    for (int col = 0; col < m; ++col) {
      int nonzeroRow = -1;
      for (int r = 0; r < n; ++r) {
        if (X[r][col]) {
          nonzeroRow = r;
          break;
        }
      }
      if (nonzeroRow == -1) continue;
      rank++;
      for (int r = nonzeroRow + 1; r < n; ++r) {
        if (X[r][col]) {
          for (int c = col; c < m; ++c) {
            X[r][c] ^= X[nonzeroRow][c];
          }
        }
      }
    }
    return rank;
  }
  void solve() {
    for (int i = 0; i < n; ++i) {
      for (int j = 0; j < m; ++j) {
        cin >> a[i][j];
      }
    }
    int rank = sweep(a);

    cout << (Modint<>(2).mpow(rank) - 1) * Modint<>(2).mpow(n - rank) * Modint<>(2).mpow(m - 1) << endl;
  }
};
